import React, { useEffect, useState } from "react";
import axios from "axios";

const List = () => {
  const [posts, setPost] = useState([]);
  const [editingPost, setEditPost] = useState(null);

  useEffect(async() => {
    await fetchPosts()
    const savedPosts = JSON.parse(localStorage.getItem('post')) || []
    setPost(savedPosts)
  }, []);

  const fetchPosts = () => {
    axios
      .get("https://jsonplaceholder.typicode.com/posts")
      .then((resp) => {
        localStorage.setItem("posts", JSON.stringify(resp.data));
      })
      .catch((err) => console.log(err));
  };
  return (
    <>
      <div>
        {posts.map((post) => (
          <div>
            <h3>{post?.title}</h3>
            <p>{post?.body}</p>
          </div>
        ))}
      </div>
    </>
  );
};

export default List;
